import React, { useEffect } from "react";
import "./CurrentDate.css";

export default function CurrentDate({ formatDate }) {
  const [hours, setHours] = useState("");
  const [minutes, setMinutes] = useState("");

  useEffect(() => {
    setHours(formatDate.getHours());
    setMinutes(formatDate.getMinutes());
  }, []);

  useEffect(() => {
    hours < 10 && setHours(`0${hours}`);
    minutes < 10 && setMinutes(`0${minutes}`);
  }, [hours, minutes]);

  var weekDays = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  var months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];
  let week = weekDays[formatDate.getDay()];
  let day = formatDate.getDate();
  let month = months[formatDate.getMonth()];
  let year = formatDate.getFullYear();
  // let hours = formatDate.getHours();
  // let minutes = formatDate.getMinutes();

  return (
    <div className="CurrentDate">
      <span className="week-day">
        {week}, {day} {month} {year}
      </span>
      <div className="time-now">
        {hours}:{minutes}
      </div>
    </div>
  );
}
