import React, { useState } from "react";
import Weather from "./Weather.js";
import axios from "axios";
import Date from "./Date";
import Footer from "./Footer";
import "./App.css";

export default function App() {
  const [searched, setSearched] = useState("");
  const [weatherData, setWeatherData] = useState({});

  return (
    <div className="content">
      <section id="frame">
        <Date week="tuesday, 1 mar 2022" time="17:20" />
        <div className="city-weather">
          <h1 className="city">{weatherData.city}</h1>

          <div className="temperature">{weatherData.temperature}ÂºC</div>

          <div className="weatherDescription">{weatherData.description}</div>
        </div>
        <input
          type="search"
          placeholder="Change City"
          className="search"
          onChange={(event) => {
            event.target.value !== ""
              ? setSearched(event.target.value)
              : setSearched("London");
          }}
          onSubmit={(event) => {
            event.preventDefault();
            let apiKey = "1f17ba351ee112a37d7633ae135f9016";
            let url = `https://api.openweathermap.org/data/2.5/weather?q=${searched}&appid=${apiKey}&units=metric`;
            axios.get(url).then((response) => {
              setWeatherData({
                temperature: Math.round(response.data.main.temp),
                city: response.data.name,
                description: response.data.weather[0].description,
                date: "tuesday, 1 mar 2022",
              });
            });
          }}
        />
      </section>
      <Footer />
    </div>
  );
}
