import React, { useState } from "react";
import axios from "axios";
import "./Forecast.css";

export default function Forecast({ coordinates }) {
  let [loaded, setLoaded] = useState(false);
  let [forecast, setForecast] = useState(null);

  if (loaded) {
    return (
      <div className="Forecast">
        {/* <a
        href="/"
        className="back-to-weather"
        onClick={() => {
          <Weather />;
        }}
      >
        <span className="back-to-weather">back</span>
      </a> */}

        <div className="forecast-grid">
          <div className="forecast-week">{forecast[0].dt}</div>
          <div>
            <span className="forecast-max-temp">
              {Math.round(forecast[0].temp.max)}ºC
            </span>{" "}
            |{" "}
            <span className="forecast-min-temp">
              {Math.round(forecast[0].temp.min)}ºC
            </span>
          </div>
          <div className="forecast-description">
            {forecast[0].weather[0].description}
          </div>
        </div>
      </div>
    );
  } else {
    let latitude = coordinates.lat;
    let longitude = coordinates.lon;
    const apiKey = "1f17ba351ee112a37d7633ae135f9016";
    let apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then((response) => {
      setForecast(response.data.daily);
      setLoaded(true);
    });
    return null;
  }
}
